INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('40', '닭불고기', '닭고기로 만든 불고기! 간장양념이 맛있어요~', '4', 'http://file.okdab.com/UserFiles/searching/recipe/007800.jpg');
