INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('42', '팥칼국수', '구수하고 담백한 팥칼국수~  걸쭉한 팥국물이 딱이예요!', '2', 'http://file.okdab.com/UserFiles/searching/recipe/008600.jpg');
INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('43', '콩나물잡채', '당면 대신 콩나물로 잡채를 만들어볼까요~', '4', 'http://file.okdab.com/UserFiles/searching/recipe/008800.jpg');
INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('44', '양배추말이찜', '양배추에 다진고기와 두부를 으깨어 넣어 맛있게 쪄서 먹기좋은 크기로 썰어보세요~', '4', 'http://file.okdab.com/UserFiles/searching/recipe/009000.jpg');
INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('45', '마늘장아찌', '마늘장아찌, 입맛 없을 때 드셔보세요~', '4', 'http://file.okdab.com/UserFiles/searching/recipe/009100.jpg');
