INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('47', '생선탕수', '필수아미노산이 풍부한 생선을 탕수육에 응용해보세요. 생선을 못먹는 어린이들에게 더없이 좋답니다.', '4', 'http://file.okdab.com/UserFiles/searching/recipe/009400.jpg');
INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('48', '갈비구이', '영양 풍부한 갈비로 푸짐한 구이 한번 해볼까요?', '4', 'http://file.okdab.com/UserFiles/searching/recipe/009900.jpg');
INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('49', '돈까스', '가장 흔한 요리를 가장 특별하게 먹을때 처럼 기분좋은 마음으로 돈까스를 만들어보세요!', '2', 'http://file.okdab.com/UserFiles/searching/recipe/010400.jpg');
INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('50', '갈비찜', '잔칫상에 갈비찜이 빠질 수 없죠!', '4', 'http://file.okdab.com/UserFiles/searching/recipe/010500.jpg');
