INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('124', '묵과양념장', '담백한 도토리묵을 새콤한 양념장과 버무리면 그맛이 환상~ 많이 먹어도 살찌지 않아요!', '2', 'http://file.okdab.com/UserFiles/searching/recipe/027300.jpg');
INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('125', '고등어살튀김과소스', '고등어를 녹말가루에 묻혀 바삭하게 튀겨 맛있는 소스와 곁들이면 정말 놀라운 맛이납니다.', '2', 'http://file.okdab.com/UserFiles/searching/recipe/027400.jpg');
INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('126', '가지와마른새우무침', '가지와 새우를 같이 볶은다음, 맛있는 양념을 하여 만들었지요. 도시락 반찬에 안성맞춤이랍니다.', '2', 'http://file.okdab.com/UserFiles/searching/recipe/027500.jpg');
INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('127', '채소말이샤브샤브', '담백하고 깊은 맛이나는 육수에 고기와 야채를 돌돌말아 살짝 익혀먹으면 입맛이 저절로 살아나요~', '2', 'http://file.okdab.com/UserFiles/searching/recipe/027600.jpg');
