INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('130', '두부양념조림', '영양가 풍부한 두부에 간장소스를 곁들이세요. 두부요리는 남녀노소 좋아하는 메뉴입니다.', '2', 'http://file.okdab.com/UserFiles/searching/recipe/028700.jpg');
