INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('132', '까르보나라스파게티', '깊고 진한 크림소스의 까르보나라 스파게티! 느끼하지 않고 부드럽습니다.', '2', 'http://file.okdab.com/UserFiles/searching/recipe/028900.jpg');
INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('133', '삼계탕', '여름철 보양식의 대표음식 삼계탕! 시원한 국물과 쫄깃한 닭고기로 몸보신하세요~', '2', 'http://file.okdab.com/UserFiles/searching/recipe/029400.jpg');
