INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('137', '떡갈비샌드위치', '인스턴트 샌드위치는 비만의 원인이랍니다. 직접 만들어 맛과 영양이 우수한 샌드위치로 건강을 챙기세요.', '2', 'http://file.okdab.com/UserFiles/searching/recipe/030300.jpg');
