INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('139', '닭고기수삼샐러드', '영양가 풍부한 닭고기와 수삼으로 고급스러운 샐러드를 만들어보세요. 즐거운 식사가 된답니다.', '2', 'http://file.okdab.com/UserFiles/searching/recipe/030700.jpg');
