INSERT INTO "main"."recipe_basic" ("recipe_id", "food_name", "summary", "serving", "food_image_URL") VALUES ('141', '과일샐러드', '향긋한 과일향이 너무 좋아요. 만들기도 참 쉽습니다.', '2', 'http://file.okdab.com/UserFiles/searching/recipe/031000.jpg');
