INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('378', '6', '어느정도 볶아지면 토마토소스, 홍합, 모시조개를 넣고 뚜껑을 덮고 중불에서 은근히 가열하고 소금, 후춧가루로 간한 후 말린 바질이 있을 경우 뿌려준다.', '');
INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('378', '4', '육수와 월계수잎을 넣고 조린 뒤 소금, 후춧가루로 간을 한다.', '');
