INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('379', '1', '돼지고기를 0.8cm 두께로 잘라서 군데군데 칼집을 넣고 편 다음 후춧가루, 소금을 약간 뿌리고 밀가루를 바른다.', '');
INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('379', '2', '계란과 우유를 혼합하여  밀가루칠한 안심을 듬뿍 적신다.', '');
