INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('379', '4', '돈까스소스, 우스터소스, 토마토케첩, 버터,  꿀, 미림, 정종, 사과, 바나나, 양파, 샐러리 1대, 마늘, 파인애플을 혼합하여 살짝 끓인다음 식혀서 겨자, 타바스코, 핫소스를 혼합하여 소스를 만든다.', '');
