INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('384', '6', '끓인 두유의 불을 끄고 한 김 나간 후 간수를 한 숟갈씩 넣어 가면서 잘 저어주면 두유가 몽글몽글 응고되며 순두부가 된다.', '');
