INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('386', '3', '여기서 다른 한쪽에서는 국수를 준비한다. 물을 끓인 후 칼국수를 넣고 쌀짝 데치듯이 삶아', '');
