INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('450', '4', '프라이팬에 베르톨리 엑스트라 라이트 올리브오일을 두르고 약불에서 계란물이 얇게 깔리도록 부어준다.', '');
