INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('452', '2', '쌀은 씻어 물에 충분히 불린 뒤 건져 물기를 뺀다.', 'http://file.okdab.com/UserFiles/searching/recipe/141500_p02.jpg');
