INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('452', '3', '냄비에 참기름을 두르고 다진 새우와 쌀을 넣고 볶다가 쌀알이 투명해지면 물을 붓고 끓인다.', '');
