INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('455', '4', '멸치액젓, 대파채, 홍고추, 통깨, 참기름을 넣어 조물조물 무쳐 소금으로 간을 맞춘다.', '');
