INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('477', '2', '당근, 감자, 표고버섯, 호박은 크기를 맞춰 다진다.', 'http://file.okdab.com/UserFiles/searching/recipe/156100_p02.jpg');
