INSERT INTO "main"."recipe_process" ("recipe_id", "process_order", "description", "process_image_URL") VALUES ('481', '1', '배추김치는 먹기 좋은 크기로 자르고 고추와 대파는 어슷썰며, 표고버섯은 길이대로 썰어 준비한다.', '');
